var fs = require('fs'),
    expect = require('chai').expect;

describe('npmrc for logger package', function () {
    it('should exist', function (done) {
        fs.stat('../../.npmrc', done);
    });

    it('must have readable and appropriate content', function () {
        let content = fs.readFileSync('../../.npmrc').toString(),
            lines = content.split('\n');

        expect(content).to.be.ok;
        expect(lines).to.include('package-lock=false');
    });
});
