const { app, session } = require('electron');
const runtime = require('.');
const sdk = require('postman-collection');

app.on('ready', function () {
  var runner = new runtime.Runner(); // runtime = require('postman-runtime');

  // a collection object constructed using the Postman Collection SDK.
  var collection = new sdk.Collection(require('./col.json'));

  try {
    runner.run(collection, {}, (err, run) => {
        run.start({
            done: () => {
                console.log('done');
            }
        });
    });
    } catch (error) {
        console.log('error while running', error)
    }
});
